def count_areas(matrix_str):
    # Convert the input string to a matrix
    matrix = [list(map(int, row.split(','))) for row in matrix_str.split(';')]


    def dfs(i, j):
        # Check if the indices are within limits and the cell value is 1 (equal to value in starting point of dfs)
        if 0 <= i < len(matrix) and 0 <= j < len(matrix[0]) and matrix[i][j] == 1:
            # Mark the cell as visited
            matrix[i][j] = -1

            # Explore all cells nearby and mark them as visited not to count the same area twice
            dfs(i - 1, j)  # Up
            dfs(i + 1, j)  # Down
            dfs(i, j - 1)  # Left
            dfs(i, j + 1)  # Right

    # Start of the count of areas
    areas_count = 0

    # Iterators walk through the matrix
    # For each matrix cell with value 1 we mark all the cells near(including start) as visited (-1) and increment counter ONLY at the first cell of an area
    # find needed areas using depth-first search and do same until we have cells with value 1!
    for i in range(len(matrix)):
        for j in range(len(matrix[0])):
            if matrix[i][j] == 1:
                areas_count += 1
                dfs(i, j)

    return areas_count

